mapper.insert:
INSERT INTO "勘定科目構成マスタ" (
    "勘定科目コード", "勘定科目パス", "更新日時", "更新者名"
) VALUES (
    #{accountCode}, #{accountPath}, CURRENT_TIMESTAMP, #{updatedBy}
)

mapper.update:
UPDATE "勘定科目構成マスタ"
SET "勘定科目パス" = #{accountPath},
    "更新日時" = CURRENT_TIMESTAMP,
    "更新者名" = #{updatedBy}
WHERE "勘定科目コード" = #{accountCode}
