mapper.insert:
INSERT INTO "課税取引マスタ" (
    "課税取引コード", "課税取引名", "税率", "更新日時", "更新者名"
) VALUES (
    #{taxCode}, #{taxName}, #{taxRate}, CURRENT_TIMESTAMP, #{updatedBy}
)

mapper.select:
SELECT * FROM "課税取引マスタ"
("課税取引コード" LIKE '%' || #{keyword} || '%' OR "課税取引名" LIKE '%' || #{keyword} || '%')
ORDER BY "課税取引コード" LIMIT #{limit} OFFSET #{offset}

mapper.update:
UPDATE "課税取引マスタ"
SET "課税取引名" = #{taxName},
    "税率" = #{taxRate},
    "更新日時" = CURRENT_TIMESTAMP,
    "更新者名" = #{updatedBy}
WHERE "課税取引コード" = #{taxCode}
