mapper.select:
SELECT * FROM "カレンダマスタ"
WHERE "カレンダコード" = #{calendarCode}
  AND "日付" = #{date}

SELECT * FROM "カレンダマスタ" WHERE 1=1
AND ("カレンダコード" LIKE '%' || #{keyword} || '%' OR "備考" LIKE '%' || #{keyword} || '%')
ORDER BY "カレンダコード", "日付" LIMIT #{limit} OFFSET #{offset}

mapper.delete:
DELETE FROM "カレンダマスタ"
WHERE "カレンダコード" = #{calendarCode}
  AND "日付" = #{date}
