mapper.select:
SELECT * FROM "原価差異データ"
("作業指示番号" LIKE '%' || #{keyword} || '%' OR "品目コード" LIKE '%' || #{keyword} || '%')
ORDER BY "作業指示番号" DESC LIMIT #{limit} OFFSET #{offset}

SELECT * FROM "原価差異データ"
WHERE "品目コード" = #{itemCode}
ORDER BY "作成日時" DESC

SELECT EXISTS(
    SELECT 1 FROM "原価差異データ"
    WHERE "作業指示番号" = #{workOrderNumber}
)

mapper.insert:
INSERT INTO "原価差異データ" (
    "作業指示番号", "品目コード",
    "材料費差異", "労務費差異", "経費差異", "総差異"
) VALUES (
    #{workOrderNumber}, #{itemCode},
    #{materialCostVariance}, #{laborCostVariance},
    #{expenseVariance}, #{totalVariance}
)
