SELECT * FROM "工数実績データ"
( "工数実績番号" LIKE '%' || #{keyword} || '%' OR "作業指示番号" LIKE '%' || #{keyword} || '%' OR "品目コード" LIKE '%' || #{keyword} || '%' OR "工程コード" LIKE '%' || #{keyword} || '%' OR "担当者コード" LIKE '%' || #{keyword} || '%' )
ORDER BY "作業日" DESC, "工数実績番号" LIMIT #{limit} OFFSET #{offset}
