mapper.insert:
INSERT INTO "ロットマスタ" (
    "ロット番号", "品目コード", "ロット種別",
    "製造日", "有効期限", "数量", "倉庫コード", "備考"
) VALUES (
    #{lotNumber}, #{itemCode},
    #{lotType, typeHandler=com.example.pms.infrastructure.out.persistence.typehandler.LotTypeTypeHandler},
    #{manufactureDate}, #{expirationDate}, #{quantity}, #{warehouseCode}, #{remarks}
)

mapper.select:
SELECT * FROM "ロットマスタ"
WHERE "品目コード" = #{itemCode}
ORDER BY "製造日" DESC

WITH RECURSIVE lot_tree AS ( SELECT lm.*, 0 AS level FROM "ロットマスタ" lm WHERE lm."ロット番号" = #{lotNumber}
UNION ALL
SELECT lm.*, lt.level + 1 FROM "ロットマスタ" lm JOIN "ロット構成" lc ON lm."ロット番号" = lc."親ロット番号" JOIN lot_tree lt ON lc."子ロット番号" = lt."ロット番号" WHERE lt.level < 10 ) SELECT * FROM lot_tree ORDER BY level ASC

WITH RECURSIVE lot_tree AS ( SELECT lm.*, 0 AS level FROM "ロットマスタ" lm WHERE lm."ロット番号" = #{lotNumber}
UNION ALL
SELECT lm.*, lt.level + 1 FROM "ロットマスタ" lm JOIN "ロット構成" lc ON lm."ロット番号" = lc."子ロット番号" JOIN lot_tree lt ON lc."親ロット番号" = lt."ロット番号" WHERE lt.level < 10 ) SELECT * FROM lot_tree ORDER BY level ASC

SELECT lm.* FROM "ロットマスタ" lm
WHERE lm."ロット番号" = #{lotNumber}
UNION
SELECT lm2.* FROM "ロットマスタ" lm2
JOIN "ロット構成" lc ON lm2."ロット番号" = lc."親ロット番号"
WHERE lc."子ロット番号" = #{lotNumber}

SELECT lm.* FROM "ロットマスタ" lm
WHERE lm."ロット番号" = #{lotNumber}
UNION
SELECT lm2.* FROM "ロットマスタ" lm2
JOIN "ロット構成" lc ON lm2."ロット番号" = lc."子ロット番号"
WHERE lc."親ロット番号" = #{lotNumber}
