mapper.insert:
INSERT INTO "工程検査結果データ" (
    "工程検査番号", "欠点コード", "数量", "備考"
) VALUES (
    #{inspectionNumber}, #{defectCode}, #{quantity}, #{remarks}
)

mapper.select:
SELECT * FROM "工程検査結果データ"
WHERE "工程検査番号" = #{inspectionNumber}
ORDER BY "欠点コード"

mapper.update:
UPDATE "工程検査結果データ" SET
    "数量" = #{quantity},
    "備考" = #{remarks}
WHERE "ID" = #{id}
