mapper.select:
SELECT * FROM "工程表"
WHERE "品目コード" = #{itemCode}
  AND "工順" = #{sequence}

SELECT * FROM "工程表"
WHERE "品目コード" = #{itemCode}
ORDER BY "工順"

SELECT * FROM "工程表"
WHERE 1=1

    AND "品目コード" = #{itemCode}

ORDER BY "品目コード", "工順"
LIMIT #{limit} OFFSET #{offset}

SELECT COUNT(*) FROM "工程表"
WHERE 1=1

    AND "品目コード" = #{itemCode}

mapper.delete:
DELETE FROM "工程表"
WHERE "品目コード" = #{itemCode}
  AND "工順" = #{sequence}
