mapper.select:
SELECT * FROM "入荷受入データ"
WHERE "発注番号" = #{purchaseOrderNumber} AND "発注行番号" = #{lineNumber}
ORDER BY "入荷日" DESC

SELECT * FROM "入荷受入データ"
AND "入荷受入区分" = #{receivingType}
AND ("入荷番号" LIKE '%' || #{keyword} || '%' OR "発注番号" LIKE '%' || #{keyword} || '%' OR "品目コード" LIKE '%' || #{keyword} || '%')
ORDER BY "入荷日" DESC, "入荷番号" DESC LIMIT #{limit} OFFSET #{offset}

SELECT COUNT(*) FROM "入荷受入データ"
AND "入荷受入区分" = #{receivingType}
AND ("入荷番号" LIKE '%' || #{keyword} || '%' OR "発注番号" LIKE '%' || #{keyword} || '%' OR "品目コード" LIKE '%' || #{keyword} || '%')
