mapper.insert:
INSERT INTO "出荷検査結果データ" (
    "出荷検査番号", "欠点コード", "数量", "備考"
) VALUES (
    #{inspectionNumber}, #{defectCode}, #{quantity}, #{remarks}
)

mapper.select:
SELECT * FROM "出荷検査結果データ"
WHERE "出荷検査番号" = #{inspectionNumber}
ORDER BY "欠点コード"

mapper.update:
UPDATE "出荷検査結果データ" SET
    "数量" = #{quantity},
    "備考" = #{remarks}
WHERE "ID" = #{id}
