mapper.select:
SELECT * FROM "担当者マスタ"
WHERE "担当者コード" = #{staffCode}
  AND "適用開始日" <= #{baseDate}
  AND ("適用停止日" IS NULL OR "適用停止日" > #{baseDate})
ORDER BY "適用開始日" DESC
LIMIT 1

SELECT * FROM "担当者マスタ"
WHERE "部門コード" = #{departmentCode}
ORDER BY "担当者コード", "適用開始日" DESC

SELECT * FROM "担当者マスタ" WHERE 1=1
AND ("担当者コード" LIKE '%' || #{keyword} || '%' OR "担当者名" LIKE '%' || #{keyword} || '%')
ORDER BY "担当者コード", "適用開始日" DESC LIMIT #{limit} OFFSET #{offset}

SELECT * FROM "担当者マスタ"
WHERE "担当者コード" = #{staffCode}
  AND "適用開始日" = #{effectiveFrom}

mapper.delete:
DELETE FROM "担当者マスタ"
WHERE "担当者コード" = #{staffCode}
  AND "適用開始日" = #{effectiveFrom}
