mapper.update:
UPDATE "在庫情報" SET
    "在庫数量" = "在庫数量" + #{quantity},
    "更新日時" = CURRENT_TIMESTAMP,
    "更新者" = #{updatedBy},
    "バージョン" = "バージョン" + 1
WHERE "場所コード" = #{locationCode}
  AND "品目コード" = #{itemCode}
  AND "バージョン" = #{expectedVersion}

UPDATE "在庫情報" SET
    "在庫数量" = "在庫数量" - #{quantity},
    "更新日時" = CURRENT_TIMESTAMP,
    "更新者" = #{updatedBy},
    "バージョン" = "バージョン" + 1
WHERE "場所コード" = #{locationCode}
  AND "品目コード" = #{itemCode}
  AND "バージョン" = #{expectedVersion}

mapper.select:
SELECT * FROM "在庫情報"
WHERE "場所コード" = #{locationCode}
  AND "品目コード" = #{itemCode}
