mapper.select:
SELECT * FROM "単価マスタ"
WHERE "品目コード" = #{itemCode}
  AND "取引先コード" = #{supplierCode}
ORDER BY "適用開始日" DESC
LIMIT 1

SELECT * FROM "単価マスタ"
WHERE "品目コード" = #{itemCode}
  AND "取引先コード" = #{supplierCode}
  AND "適用開始日" <= #{baseDate}
  AND ("適用停止日" IS NULL OR "適用停止日" > #{baseDate})
ORDER BY "適用開始日" DESC
LIMIT 1

SELECT * FROM "単価マスタ"
WHERE "品目コード" = #{itemCode}
ORDER BY "取引先コード", "適用開始日" DESC

mapper.update:
UPDATE "単価マスタ" SET
    "単価" = #{price},
    "通貨コード" = #{currencyCode},
    "更新日時" = CURRENT_TIMESTAMP
WHERE "品目コード" = #{itemCode}
  AND "取引先コード" = #{supplierCode}
  AND "適用開始日" = #{effectiveFrom}
