mapper.select:
SELECT * FROM "賃率マスタ"
WHERE "作業者区分コード" = #{workerCategoryCode}
ORDER BY "適用開始日" DESC

SELECT * FROM "賃率マスタ"
WHERE "作業者区分コード" = #{workerCategoryCode}
  AND "適用開始日" <= #{targetDate}
  AND ("適用終了日" IS NULL OR "適用終了日" >= #{targetDate})
ORDER BY "適用開始日" DESC
LIMIT 1

mapper.insert:
INSERT INTO "賃率マスタ" (
    "作業者区分コード", "作業者区分名", "適用開始日", "適用終了日",
    "時間単価", "直接労務フラグ"
) VALUES (
    #{workerCategoryCode}, #{workerCategoryName}, #{effectiveStartDate},
    #{effectiveEndDate}, #{hourlyRate}, #{isDirect}
)
