mapper.insert:
INSERT INTO "顧客別販売単価" (
    "商品コード", "取引先コード", "適用開始日", "適用終了日", "販売単価",
    "作成日時", "作成者名", "更新日時", "更新者名"
) VALUES (
    #{productCode}, #{partnerCode}, #{startDate}, #{endDate}, #{sellingPrice},
    CURRENT_TIMESTAMP, #{createdBy}, CURRENT_TIMESTAMP, #{updatedBy}
)

mapper.select:
SELECT * FROM "顧客別販売単価"
WHERE "商品コード" = #{productCode}
  AND "取引先コード" = #{partnerCode}
  AND "適用開始日" = #{startDate}

SELECT * FROM "顧客別販売単価"
WHERE "商品コード" = #{productCode}
ORDER BY "取引先コード", "適用開始日" DESC

SELECT * FROM "顧客別販売単価"
WHERE "取引先コード" = #{partnerCode}
ORDER BY "商品コード", "適用開始日" DESC

mapper.update:
UPDATE "顧客別販売単価" SET
    "適用終了日" = #{endDate},
    "販売単価" = #{sellingPrice},
    "更新日時" = CURRENT_TIMESTAMP,
    "更新者名" = #{updatedBy}
WHERE "商品コード" = #{productCode}
  AND "取引先コード" = #{partnerCode}
  AND "適用開始日" = #{startDate}

mapper.delete:
DELETE FROM "顧客別販売単価"
WHERE "商品コード" = #{productCode}
  AND "取引先コード" = #{partnerCode}
  AND "適用開始日" = #{startDate}
