mapper.insert:
INSERT INTO "部門マスタ" (
    "部門コード", "開始日", "終了日", "部門名",
    "組織階層", "部門パス", "最下層区分",
    "作成日時", "作成者名", "更新日時", "更新者名"
) VALUES (
    #{departmentCode}, #{startDate}, #{endDate}, #{departmentName},
    #{hierarchyLevel}, #{departmentPath}, #{isLeaf},
    CURRENT_TIMESTAMP, #{createdBy}, CURRENT_TIMESTAMP, #{updatedBy}
)

mapper.select:
SELECT * FROM "部門マスタ"
WHERE "部門コード" = #{departmentCode}
  AND "開始日" <= #{baseDate}
  AND ("終了日" IS NULL OR "終了日" > #{baseDate})
ORDER BY "開始日" DESC
LIMIT 1

SELECT * FROM "部門マスタ"
("終了日" IS NULL OR "終了日" > CURRENT_DATE)
AND "組織階層" = #{level}
AND (LOWER("部門コード") LIKE LOWER('%' || #{keyword} || '%') OR LOWER("部門名") LIKE LOWER('%' || #{keyword} || '%'))
ORDER BY "部門パス", "開始日" DESC LIMIT #{limit} OFFSET #{offset}

mapper.update:
UPDATE "部門マスタ" SET
    "終了日" = #{endDate},
    "部門名" = #{departmentName},
    "組織階層" = #{hierarchyLevel},
    "部門パス" = #{departmentPath},
    "最下層区分" = #{isLeaf},
    "更新日時" = CURRENT_TIMESTAMP,
    "更新者名" = #{updatedBy}
WHERE "部門コード" = #{departmentCode}
  AND "開始日" = #{startDate}
