    "社員コード", "社員名", "社員名カナ", "部門コード", "開始日",
    "作成日時", "作成者名", "更新日時", "更新者名"
) VALUES (
    #{employeeCode}, #{employeeName}, #{employeeNameKana}, #{departmentCode}, #{departmentStartDate},
    CURRENT_TIMESTAMP, #{createdBy}, CURRENT_TIMESTAMP, #{updatedBy}
)

mapper.select:
SELECT * FROM "社員マスタ"
WHERE "部門コード" = #{departmentCode}
ORDER BY "社員コード"

