"部門コード" = #{departmentCode}
AND (LOWER("社員コード") LIKE LOWER('%' || #{keyword} || '%') OR LOWER("社員名") LIKE LOWER('%' || #{keyword} || '%') OR LOWER("社員名カナ") LIKE LOWER('%' || #{keyword} || '%'))
ORDER BY "社員コード" LIMIT #{limit} OFFSET #{offset}

SELECT COUNT(*) FROM "社員マスタ"
"部門コード" = #{departmentCode}
