mapper.insert:
INSERT INTO "商品マスタ履歴" (
    "商品コード", "有効開始日", "有効終了日", "商品名",
    "商品区分", "単価", "税区分", "作成日時", "作成者"
) VALUES (
    #{productCode}, #{validFromDate}, #{validToDate}, #{productName},
    #{productCategory, typeHandler=com.example.sms.infrastructure.out.persistence.typehandler.ProductCategoryTypeHandler}::商品区分,
    #{unitPrice},
    #{taxCategory, typeHandler=com.example.sms.infrastructure.out.persistence.typehandler.TaxCategoryTypeHandler}::税区分,
    CURRENT_TIMESTAMP, #{createdBy}
)

mapper.select:
SELECT * FROM "商品マスタ履歴"
WHERE "商品コード" = #{productCode}
ORDER BY "有効開始日" DESC

SELECT * FROM "商品マスタ履歴"
WHERE "商品コード" = #{productCode}
AND "有効開始日" <= #{targetDate}
AND ("有効終了日" IS NULL OR "有効終了日" >= #{targetDate})
ORDER BY "有効開始日" DESC
LIMIT 1
