mapper.insert:
INSERT INTO "出荷先マスタ" (
    "取引先コード", "顧客枝番", "出荷先番号", "出荷先名", "地域コード",
    "出荷先郵便番号", "出荷先住所1", "出荷先住所2",
    "作成日時", "作成者名", "更新日時", "更新者名"
) VALUES (
    #{partnerCode}, #{customerBranchNumber}, #{shippingNumber}, #{shippingName}, #{regionCode},
    #{postalCode}, #{address1}, #{address2},
    CURRENT_TIMESTAMP, #{createdBy}, CURRENT_TIMESTAMP, #{updatedBy}
)

mapper.select:
SELECT * FROM "出荷先マスタ"
WHERE "取引先コード" = #{partnerCode}
  AND "顧客枝番" = #{customerBranchNumber}
  AND "出荷先番号" = #{shippingNumber}

SELECT * FROM "出荷先マスタ"
WHERE "取引先コード" = #{partnerCode}
  AND "顧客枝番" = #{customerBranchNumber}
ORDER BY "出荷先番号"

mapper.update:
UPDATE "出荷先マスタ" SET
    "出荷先名" = #{shippingName},
    "地域コード" = #{regionCode},
    "出荷先郵便番号" = #{postalCode},
    "出荷先住所1" = #{address1},
    "出荷先住所2" = #{address2},
    "更新日時" = CURRENT_TIMESTAMP,
    "更新者名" = #{updatedBy}
WHERE "取引先コード" = #{partnerCode}
  AND "顧客枝番" = #{customerBranchNumber}
  AND "出荷先番号" = #{shippingNumber}

mapper.delete:
DELETE FROM "出荷先マスタ"
WHERE "取引先コード" = #{partnerCode}
  AND "顧客枝番" = #{customerBranchNumber}
  AND "出荷先番号" = #{shippingNumber}

DELETE FROM "出荷先マスタ"
WHERE "取引先コード" = #{partnerCode}
  AND "顧客枝番" = #{customerBranchNumber}
