mapper.insert:
INSERT INTO "仕入先マスタ" (
    "仕入先コード", "仕入先枝番", "仕入先担当者名", "部門名",
    "電話番号", "FAX番号", "メールアドレス",
    "作成日時", "作成者名", "更新日時", "更新者名"
) VALUES (
    #{supplierCode}, #{supplierBranchNumber}, #{representativeName}, #{departmentName},
    #{phone}, #{fax}, #{email},
    CURRENT_TIMESTAMP, #{createdBy}, CURRENT_TIMESTAMP, #{updatedBy}
)

mapper.select:
SELECT * FROM "仕入先マスタ"
WHERE "仕入先コード" = #{supplierCode}
  AND "仕入先枝番" = #{branchNumber}

SELECT * FROM "仕入先マスタ"
WHERE "仕入先コード" = #{supplierCode}
ORDER BY "仕入先枝番"

SELECT * FROM "仕入先マスタ"
(LOWER("仕入先コード") LIKE LOWER('%' || #{keyword} || '%') OR LOWER("仕入先担当者名") LIKE LOWER('%' || #{keyword} || '%') OR LOWER("部門名") LIKE LOWER('%' || #{keyword} || '%'))
ORDER BY "仕入先コード", "仕入先枝番" LIMIT #{limit} OFFSET #{offset}

mapper.update:
UPDATE "仕入先マスタ" SET
    "仕入先担当者名" = #{representativeName},
    "部門名" = #{departmentName},
    "電話番号" = #{phone},
    "FAX番号" = #{fax},
    "メールアドレス" = #{email},
    "更新日時" = CURRENT_TIMESTAMP,
    "更新者名" = #{updatedBy}
WHERE "仕入先コード" = #{supplierCode}
  AND "仕入先枝番" = #{supplierBranchNumber}

mapper.delete:
DELETE FROM "仕入先マスタ"
WHERE "仕入先コード" = #{supplierCode}
  AND "仕入先枝番" = #{branchNumber}
